    public boolean validateName(String name) {
        Pattern ptr = Pattern.compile("^[\\p{L} \\.'\\-]+$");
        if (ptr.matcher(name).matches()) {
            return true;
        }

        return false;
    }

    public boolean validateNid(String nid) {

        Pattern ptr = Pattern.compile("[\\d]{6}/[\\d]{4}");
        if (ptr.matcher(nid).matches()) {
            return true;
        }

        return false;
    }
